const initialState = {
  hours: 0,
  date: "",
};

const CalculateDateReducer = (state = initialState, action) => {
  if (action.type === "CALCULATE_DATE") {
    const today = new Date();
    const hours = today.getUTCHours() + +action.payload;
    const utcDate = today.getUTCDate();
    const month = today.getMonth();
    const year = today.getFullYear();
    const monthNames = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    if (hours >= 24) {
      state.date =
        String(utcDate + 1) +
        "th of " +
        monthNames[month] +
        ", " +
        String(year);
      state.hours = hours;
      console.log(state.date);
      console.log(state.hours);
      return {
        hours: state.hours,
        date: state.date,
      };
    } else if (hours < 0) {
      state.date =
        String(utcDate - 1) +
        "th of " +
        monthNames[month] +
        ", " +
        String(year);
      state.hours = hours;
      console.log(state.date);
      console.log(state.hours);
      return {
        hours: state.hours,
        date: state.date,
      };
    } else if (0 < hours < 24) {
      state.date =
        String(utcDate) + "th of " + monthNames[month] + ", " + String(year);
      state.hours = hours;
      console.log(state.date);
      console.log(state.hours);
      return {
        hours: state.hours,
        date: state.date,
      };
    }
  }
  return state;
};

export default CalculateDateReducer;
