const initialState = {
  hours: "",
  minutes: "",
  seconds: "",
  date: "",
};

const GetInputReducer = (state = initialState, action) => {
  if (action.type === "CALCULATE_DATE") {
    const today = new Date();
    const hours = today.getUTCHours() + +action.payload;
    const date = today.getUTCDate();
    const month = today.getMonth();
    const year = today.getFullYear();
    const monthNames = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    if (hours >= 24) {
      state.date =
        String(date + 1) + "th of " + monthNames[month] + ", " + String(year);
      return {
        hours: state.hours,
        minutes: state.minutes,
        seconds: state.seconds,
        date: state.date,
      };
    } else if (hours < 0) {
      state.date =
        String(date - 1) + "th of " + monthNames[month] + ", " + String(year);
      return {
        hours: state.hours,
        minutes: state.minutes,
        seconds: state.seconds,
        date: state.date,
      };
    } else {
      state.date =
        String(date) + "th of " + monthNames[month] + ", " + String(year);
      return {
        hours: state.hours,
        minutes: state.minutes,
        seconds: state.seconds,
        date: state.date,
      };
    }
  }
  if (action.type === "CALCULATE_TIME") {
    const today = new Date();
    const minutes = today.getUTCMinutes();
    const seconds = today.getUTCSeconds();
    const calculatedTime = (Hours, Minutes, Seconds) => {
      let min = ((Hours * 60) % 60) + Math.floor(Minutes);
      let hours = Math.floor(Hours);
      let sec = ((Minutes * 60) % 60) + Seconds;
      if (min >= 60) {
        const rH = String(hours + 1);
        const rM = String(min - 60);
        if (sec >= 60) {
          const rH = String(hours + 1);
          const rM = String(min - 60 + 1);
          const rS = String(sec - 60);
          return rH + " hours " + rM + " minutes " + rS + " seconds";
        }
        return rH + " hours " + rM + " minutes " + sec + " seconds";
      } else {
        if (sec >= 60) {
          const rH = String(hours);
          const rM = String(min + 1);
          const rS = String(sec - 60);
          return rH + " hours " + rM + " minutes " + rS + " seconds";
        }
        return (
          String(hours) +
          " hours " +
          String(min) +
          " minutes " +
          sec +
          " seconds"
        );
      }
    };
  }
  return state;
};

export default GetInputReducer;
