import React, { useEffect, useState } from "react";
import styles from "./Input.module.css";
import FetchFunctions from "../../FetchDataFromBackend/FetchFunctions";

const Input = () => {
  const { regionData, FetchRegions, FetchRegion } = FetchFunctions();
  const [searchValue, setSearchValue] = useState("");

  useEffect(() => {
    FetchRegions();
    //eslint-disable-next-line
  }, []);

  const changeHandler = (e) => {
    setSearchValue(e.target.value);
  };

  const clickHandler = (e) => {
    e.preventDefault();
    setSearchValue("");
    FetchRegion(e.target.id);
  };

  return (
    <div className={styles.input}>
      <input
        type="text"
        onChange={changeHandler}
        placeholder="Search for the location"
        value={searchValue}
      />
      <div className={styles.list}>
        <ul>
          {regionData
            .filter((searchedRegion) => {
              if (searchValue === "") {
                return searchedRegion;
              } else if (
                searchedRegion.name
                  .toLowerCase()
                  .includes(searchValue.toLowerCase())
              ) {
                return searchedRegion;
              }
            })
            .map((region) => (
              <li onClick={clickHandler} id={region.id} key={region.id}>
                {region.name}
              </li>
            ))}
        </ul>
      </div>
    </div>
  );
};

export default Input;
