import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

const FetchFunctions = () => {
  const [regionData, setRegionData] = useState([]);
  const utcOffset = useSelector((state) => state.SendOutputReducer.utcOffset);
  const { hours, minutes, seconds } = useSelector(
    (state) => state.GetInputReducer
  );
  const dispatch = useDispatch();

  const FetchRegions = async () => {
    const response = await fetch("http://localhost:5000/api");
    const data = await response.json();
    setRegionData((prevState) => [...prevState, ...data.regions]);
  };

  const FetchRegion = async (id) => {
    const response = await fetch(`http://localhost:5000/api/${id}`);
    const data = await response.json();
    dispatch({
      type: "LOCATION_DETAILS",
      payload: data,
    });
  };

  const calculateDateAndTime = () => {
    dispatch({
      type: "CALCULATE_DATE",
      payload: utcOffset,
    });
    dispatch({
      type: "CALCULATE_TIME",
      payload: {
        hours: hours,
        minutes: minutes,
        seconds: seconds,
      },
    });
  };

  return {
    regionData,
    FetchRegions,
    FetchRegion,
    calculateDateAndTime,
  };
};

export default FetchFunctions;
